package abc;

public class pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int n = 5;

	        for(int i = n; i >= 1; i--)
	        {  //For Loop for Row 

	            for(int j = 1; j <= i; j++)
	            { //For Loop for Col

	                System.out.print("* "); //Prints *

	            }

	            System.out.println(); //Get to newline

	        }

	    }

	}